binding.script:
if(getprop("/gear/gear/compression-ft") > 0.01){
	if(getprop("/payload/armament/msg")){screen.log.write("Damage OFF",1,0,0);setprop("/payload/armament/msg",0);}else{screen.log.write("Damage ON",1,0,0);setprop("/payload/armament/msg",1);}
}else{
	screen.log.write("Damage can be toggled only when on the ground! Sorry! Its just the way it is.",1,0,0);
}

if(getprop("/gear/gear/compression-ft") > 0.01){
	f22.repair();
	guns.reload();
}else{
	screen.log.write("You can only repair the aircraft while on the ground! The ground crew cant fly!.",1,0,0);
}

if(getprop("/gear/gear/compression-ft") > 0.01){
	if(getprop("/payload/armament/oldmsg")){screen.log.write("Old Damage OFF",1,0,0);setprop("/payload/armament/oldmsg",0);}else{screen.log.write("Old Damage ON",1,0,0);setprop("/payload/armament/oldmsg",1);}
}else{
	screen.log.write("Damage can be toggled only when on the ground! Sorry! Its just the way it is.",1,0,0);
}
